import React, { useEffect, useState } from 'react';

function App() {
  const [state, setState] = useState({
    name: '',
    password: '',
  });

  const [date, setDate] = useState({
    name: '',
    password: '',
  });

  useEffect(() => {
    fetch(`http://localhost:5000/getServer?name=${state.name}&password=${state.password}`, {
  method: 'GET',
  headers: {
    'Content-Type': 'application/json',
  },
})

      .then((response) => response.json())
      .then((response) =>
        setDate({
          name: response.name,
          password: response.password,
        })
      )
      .catch((error) => console.log(error));
  }, []);

  const onChange = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const register = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/login", {
      method: 'POST',
      body: JSON.stringify({
        name: state.name,
        password: state.password,
      }),
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((response) => console.log(response)) // Додайте логіку обробки відповіді від сервера
      .catch((error) => console.log(error));
  };

  return (
    <div>
      <form>
        <label htmlFor='name'>Name</label>
        <input
          type='text'
          value={state.name}
          name='name'
          placeholder='Name'
          onChange={onChange}
          id='name'
        />

        <label htmlFor='password'>Password</label>
        <input
          type='password'
          value={state.password}
          name='password'
          placeholder='Password'
          onChange={onChange}
          id='password'
        />
        <button onClick={register}>Send</button>
      </form>

      <div>
        {date.name && date.password ? (
          <div>
            <h2>Data from Server:</h2>
            <p>Name: {date.name}</p>
            <p>Password: {date.password}</p>
          </div>
        ) : (
          'Error fetching data from the server'
        )}
      </div>
    </div>
  );
}

export default App;
